filteredlist=[] #List where the countries that meet the user's criteria will be stored

import random #used to generate a random country
from PIL import Image #allows images to be displayed
import requests #allows images to be displayed
from io import BytesIO #allows images to be displayed
import random #allows images to be displayed

#Functions
def open_image(url): #Code given by instructor for the purpose of displaying images
    response = requests.get(url)
    img = Image.open(BytesIO(response.content))
    img.show()

def population(user): #Function uses feedback from the user in order to generate a random country that meets the given population criteria
    user=user.lower() #converts user feedback into lowercase
    try:
        if user=="minimum" or user=="min":
            print("\n")
            min=(int(input("What is your minimum population preference? ")))
            for i in range(len(populations)): #repeats loop for the length of the list
                if populations[i]>min:
                    filteredlist.append(countries[i]) #adds the country to the filtered list if the population is above the desired minimum
        if user=="maximum" or user=="max":
            print("\n")
            max=(int(input("What is your maximum population preference? ")))
            for i in range(len(populations)):
                if populations[i]<max:
                    filteredlist.append(countries[i]) #adds the country to the filtered list if the population is above the desired maximum
        print("\n")
        choice=random.choice(filteredlist)
        print("You should go to "+choice+"!") #Displays a random country from the list that meet the user's population preferences
        index=countries.index(choice)
        open_image(flags[index]) #displays the image for the suggested country; image found by source [1]
    except:
        print("You have entered an invalid input. Please try again.") #if the user enters an invalid input, the program will prompt the user to try again

#Main
print("\n")
print("-------------------------------------------------------------Random Country Generator-------------------------------------------------------------")
print("\n")
print("Welcome to the Random Country Generator! This program will allow you to pick a random country to go to based on your unique population preference.")
print("\n")
population(input("Would you like to enter a minimum or maximum value of your preferred population? "))

